    private void btnRellenarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRellenarActionPerformed
        try{
            DefaultComboBoxModel modelo = new DefaultComboBoxModel();
            if(Integer.parseInt(txtIntervalo.getText())<=0 || Integer.parseInt(txtFin.getText())<Integer.parseInt(txtInicio.getText())){
                throw new Exception();
            }
            int num=Integer.parseInt(txtInicio.getText());
            while(num<=Integer.parseInt(txtFin.getText())){
                modelo.addElement(num);
                num+=Integer.parseInt(txtIntervalo.getText());
            }
            cboProgresion.setModel(modelo);
        }catch(Exception exception){
            JOptionPane.showMessageDialog(null,"ERROR");
        }
    }//GEN-LAST:event_btnRellenarActionPerformed

    private void btnVaciarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVaciarActionPerformed
        DefaultComboBoxModel modelo = new DefaultComboBoxModel();
        cboProgresion.setModel(modelo);
        etiResultado.setText("");
    }//GEN-LAST:event_btnVaciarActionPerformed

    private void cboProgresionItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cboProgresionItemStateChanged
        etiResultado.setText(cboProgresion.getSelectedItem().toString());
    }//GEN-LAST:event_cboProgresionItemStateChanged

