    private void btnAnalizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAnalizarActionPerformed
        if(txtFrase.getText().equals("") || txtPalabra1.getText().equals("")|| txtPalabra2.getText().equals("")|| txtPalabra3.getText().equals("")|| txtPalabra4.getText().equals("")){
            JOptionPane.showMessageDialog(null,"ERROR");
        }else{
        if(txtFrase.getText().equals(txtPalabra1.getText())){
            etiA.setText("La frase es igual a la palabra 1");
        }else{
            etiA.setText("La frase no es igual a la palabra 1");
        }
        if(txtFrase.getText().startsWith(txtPalabra2.getText())){
            etiB.setText("La frase empieza con la palabra 2");
        }else{
            etiB.setText("La frase no empieza con la palabra 2");
        }
        if(txtFrase.getText().endsWith(txtPalabra3.getText())){
            etiC.setText("La frase termina con la palabra 3");
        }else{
            etiC.setText("La frase no termina con la palabra 3");
        }
        if(txtFrase.getText().contains(txtPalabra4.getText())){
            etiD.setText("La frase contiene la palabra 4 en la posici√≥n "+txtFrase.getText().indexOf(txtPalabra4.getText()));
        }else{
            etiD.setText("La frase no contiene la palabra 4");
        }}
    }//GEN-LAST:event_btnAnalizarActionPerformed

