    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        double x=0;
        double a=Double.parseDouble(txtA.getText());
        double b=Double.parseDouble(txtB.getText());
        try{
            if(a<0 || a>100 || b<0 || b>100){
                throw new Exception();
            }
            if(a>b){
                if(b==0){
                    throw new NullPointerException();
                }
                x=Double.parseDouble(txtA.getText())/Double.parseDouble(txtB.getText());
            }else{
                if(a==0){
                    throw new NullPointerException();
                }
                x=Double.parseDouble(txtB.getText())/Double.parseDouble(txtA.getText());
            }
            etiResultado.setText(String.format("%.3f",x));
        }catch(NullPointerException ex){
            JOptionPane.showMessageDialog(null,"el menor no deber ser 0");
        }catch(Exception exception){
            JOptionPane.showMessageDialog(null,"Solo números entre 0 y 100");
        }
    }//GEN-LAST:event_btnCalcularActionPerformed

