    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        try{
            int total=0;
            int unidades=Integer.parseInt(txtUnidades.getText());
            if(lstProductos.getSelectedIndex()==-1){
                throw new Exception();
            }else if(lstProductos.getSelectedIndex()==0){
                total+=120*unidades;
            }else if(lstProductos.getSelectedIndex()==1){
                total+=60*unidades;
            }else if(lstProductos.getSelectedIndex()==2){
                total+=45*unidades;
            }
            if(cboDestino.getSelectedIndex()==0){
                total+=20;
            }else if(cboDestino.getSelectedIndex()==1){
                total+=25;
            }else if(cboDestino.getSelectedIndex()==2){
                total+=30;
            }
            if(cboTipoEnvio.getSelectedIndex()==0){
                total+=3;
            }else if(cboTipoEnvio.getSelectedIndex()==1){
                total+=5;
            }
            etiResultado.setText("su total es $"+total);
        }catch(Exception exception){
            JOptionPane.showMessageDialog(null,"ERROR");
        }
    }//GEN-LAST:event_btnCalcularActionPerformed

